import React, { useState } from 'react';
import axios from 'axios';

function LoginForm({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://127.0.0.1:8000/api/auth/jwt/create/', {
        username, password
      });
      onLogin(res.data.access);
    } catch {
      setError('Неправильний логін або пароль');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Увійти</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input value={username} onChange={e => setUsername(e.target.value)} placeholder="Ім'я" required /><br />
      <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Пароль" required /><br />
      <button type="submit">Увійти</button>
    </form>
  );
}

export default LoginForm;
