import React, { useState } from 'react';
import axios from 'axios';

function CreateEventForm({ token, onEventCreated }) {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    start_time: '',
    end_time: ''
  });

  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await axios.post('http://127.0.0.1:8000/api/events/', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      onEventCreated();
    } catch (err) {
      setError('Помилка створення події');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Створити подію</h3>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input name="title" placeholder="Назва" onChange={handleChange} required /><br />
      <textarea name="description" placeholder="Опис" onChange={handleChange}></textarea><br />
      <input name="start_time" type="datetime-local" onChange={handleChange} required /><br />
      <input name="end_time" type="datetime-local" onChange={handleChange} required /><br />
      <button type="submit">Створити</button>
    </form>
  );
}

export default CreateEventForm;
