        <option value="false">–ù–µ–≤–∏–∫–æ–Ω–∞–Ω—ñ</option>
        <option value="true">–í–∏–∫–æ–Ω–∞–Ω—ñ</option>
      </select>
      <ul>
        {tasks.map(task => (
          <li key={task.id}>
            <strong>{task.title}</strong> ‚Äì {task.is_completed ? '‚úÖ' : '‚ùå'}
            <button onClick={() => toggleComplete(task.id, task.is_completed)}>
              {task.is_completed ? '–ü–æ–∑–Ω–∞—á–∏—Ç–∏ —è–∫ –Ω–µ–≤–∏–∫–æ–Ω–∞–Ω–µ' : '–í–∏–∫–æ–Ω–∞–Ω–æ'}
            </button>
